import { io } from "socket.io-client"

const utils = require("../utils/utils")

export function initSocket(){
    if(!this.state.isLoggedIn){
        return false
    }

    if(typeof window.customVariables.socket !== "undefined"){
        return false
    }

    window.customVariables.socket = io("https://socket.filen.io", {
		path: "",
		reconnect: true,
		transports: [
			"websocket"
		]
	})

    window.customVariables.socket.on("connect", () => {
        console.log("Connected to socket")
    })

    window.customVariables.socket.on("disconnect", () => {
        console.log("Disconnected from socket")
    })

    window.customVariables.socket.on("message", (data) => {
        console.log(message)
    })
}

export function sendSocket(messsage, data){
    if(!this.state.isLoggedIn){
        return false
    }

    if(typeof window.customVariables.socket == "undefined"){
        return false
    }

    window.customVariables.socket.emit(message, data)

    return true
}
